--5.Print Number of Emplyoees by CountryRegionNameUSE AdventureWorks2017SELECTPCR.[Name]  Country_Region_Name, COUNT (HRE.BusinessEntityID) Number_of_EmployeesFROM[Person].[CountryRegion] PCR JOIN[Person].[StateProvince] PSP ON PCR.CountryRegionCode=PSP.CountryRegionCode JOIN[Person].[Address] PA ON PSP.[StateProvinceID]=PA.StateProvinceID  JOIN[Person].[BusinessEntityAddress] PBEA ONPBEA.AddressID=PA.AddressID  JOIN[HumanResources].[Employee] HRE ON HRE.BusinessEntityID=PBEA.BusinessEntityIDGROUP BYPCR.[Name] ORDER BY Number_of_Employees DESC;